pkg load statistics% clase 2 ej 2rho = 0.5;sigma2_x = 1;sigma2_y = sigma2_x / 2;% numero de realizacionesN = 1000;mu = zeros(2,1);sigma2 = [sigma2_x                                                     rho*sigma2_x*sigma2_y;                                rho*sigma2_x*sigma2_y    sigma2_y];XY = mvnrnd(mu,sigma2,N);W = [ 1 1 ; -1 1 ];%W = [ 1 1 ; 1 1 ];%W = [ -1  -1 ; 1 1 ];UV   = zeros(size(XY));for i=1:N      UV(i,:) = W* (XY(i,:) )';end% dibujar X vs Yscatter(UV(:,1),UV(:,2))