% clase 3 ej 3rand("seed",123);% numero de simulacionesN = 10000;X = rand(N,1);Y = rand(N,1);Z = X+Y; %  encontrar posiciones donde X<delta para encontrar la esperanza condicional delta = 0.05;  X_index = find(X<delta); Z_index = find(Z<delta);  % calcular las esperanzas condicionales a que X o Z cumplan la condicion < delta  % E[Z|X] E_Z_dado_X = mean(Z(X_index))   % E[X|Z] E_X_dado_Z = mean(X(Z_index))  % E[XZ|X] E_XZ_dado_X = mean(X(X_index).*Z(X_index))  % E[XZ|Z] E_XZ_dado_Z = mean(X(Z_index).*Z(Z_index))