% clase 3 ej 3rand("seed",123);% numero de simulacionesN = 10000;X = rand(N,1);Y = rand(N,1);Z = X+Y; %  encontrar posiciones donde X<delta para encontrar la esperanza condicional delta = 1e-2; value = 0.05;  % simulo una medicion X=x=value X_index = find((X>value-delta) & (X< value + delta)); Z_index = find((Z>value-delta) & (Z< value + delta));  % calcular las esperanzas condicionales a que X o Z cumplan la condicion < delta  % E[Z|X] E_Z_dado_X = mean(Z(X_index))   % E[X|Z] E_X_dado_Z = mean(X(Z_index))  % E[XZ|X] E_XZ_dado_X = mean(X(X_index).*Z(X_index))  % E[XZ|Z] E_XZ_dado_Z = mean(X(Z_index).*Z(Z_index))