pkg load statisticsrandn("seed", 1234)% numero de simulacionesn = 2000;% numero de muestrasN = 100;% media muestral% la usamos como media de distribucion en el ejerciciomu = 180;% dispersionsigma = sqrt(49);% el intervalo de confianza de 95% teorico es:mu_min_teorico = mu - 1.96*sigma/sqrt(N);mu_max_teorico = mu + 1.96*sigma/sqrt(N);% nivel de confianza del intervalo, deberia ser igual a 0.95 al final de la simulacionconfianza = 0;for i=1:n    % generar  una muestra de la distribucion de medias muestrales    X = sigma/sqrt(N)*randn+ mu;    %  chequeo cuantos valores estan en el intervalo de confianza    confianza = confianza + (1/n) *(X>=mu_min_teorico & X<=mu_max_teorico);end% estimacion del intervalo de confianzaconfianza_simulado = confianza confianza_teorico = 0.95