pkg load statisticsclose all% numero de simulacionesn = 1000;% vector de probabilidadesp = linspace(0,1,n);% uniformealpha=1;beta=1;plot(p, betapdf(p,alpha,beta))figure% parecido a normalalpha=5;beta=5;p = linspace(-3,3,n);plot(p, betapdf(p,alpha,beta))figure% parecido a exponencial negativaalpha=1;beta=3;p = linspace(0,1,n);plot(p, betapdf(p,alpha,beta))