% clase 7 ej 1rand("seed",1234);% numero de simulacionesn = 1000;% numero de ticketsN_tickets = 100;% numero de tickets con premioN_premios = 40;% Cantidad de veces que ganamos 2 premios exactamenteY = 0;% ticket con premio =1, sin premio =0tickets= [ones(N_premios,1) ; zeros(N_tickets-N_premios,1)];% hago que los premios aparezcan en posiciones aleatoriastickets = tickets(randperm(length(tickets)));% cantidad de experimentosfor i =1:n    % X: cantidad de premios    X = 0;    % elegimos 3tickets al azar    for j=1:3          % elijo posiciones al azar de los 100 tickets disponibles          % estrictamente habria que chequear que no elegimos el mismo indice 2 veces!          X = X + tickets(randi(100));
    endfor
    % chequeo si ganamos exactamente 2 premios    if(X==2)      Y = Y + 1;    end      endfor    % estimacion de probabilidad de ganar 2 tickets  P_GanarExactamente2Premios = Y/n