% configuro la semilla inicial del proceso aleatorio uniformerand ("seed", 123);% numero de simulacionesN=100;% simular una variable aleatoria uniforme cero unoU = rand(N,1);% genero la variable aleatoria pedidaX= sqrt(U);% media de Xmean(X)% varianza de Xvar(X)% simulaciones de la variable independienten = 1000;% variable independientex = linspace(-0.1,1.1,n);% constante de filtrado (y dispersion de la Gaussiana)h = 0.1;% proceso aleatorio%  pdf estimadap = zeros(size(x));% para cada muestrafor i=1:N  % para cada variable independiente  for j=1:length(x)        % kernel function, Gaussian        K = (1/sqrt(2*pi))*exp(-(1/2)*((X(i)-x(j))/h)^2);        % estimation of pdf        p(j) = p(j) + (1/(N*h)) * K;    endforendfor      plot(x,p)