pkg  load statisticsclose all% configuro la semilla inicial del proceso aleatorio uniformerandn ("seed", 123);% numero de simulacionesN=100;% simular una variable XX = 5*linspace(1,N,N)';% ruidosigma = 10;W = sigma * randn(N,1);% genero la variable aleatoria Y = 5X + WY = 5*X + W;% matriz de regresion linealA = [X    ones(N,1)];% coeficientes de regresion linealbeta = pinv(A)*Y% estimacion de Y=5XY_hat = A*beta;scatter(X,Y)hold onplot(X,Y_hat)% otra formabeta1 = (X'*Y - 1/N * X'*((Y'*ones(N,1))*ones(N,1)))/(X'*X - 1/N * (X'*ones(N,1))^2);beta0 = mean(Y) - beta1*mean(X)';beta2 = [beta1 ; beta0]