% clase 3 ej 2% parte teorica% E[Y] = L/2, distribucion uniforme% E[X|Y] = Y/2, distribution uniforme% Ley de esperanzas iteradas:% E[X] = E[E[X|Y]] = E[Y/2] = E[Y]/2 = L/4rand("seed",123);% numero de simulacionesN = 10000;X = zeros(N,1);% longitud de la varaL = 1;for i=1:N    %  corto la vara en una posicion aleatoria uniforme [0,1]    Y = L * rand;    % corto lo que queda de la vara en una posicion aleatoria uniforme [0,1]    X(i) = Y * rand;endformean(X)X_mean_teorica = L/4