% clase 3 ej 4rand("seed",123);% numero de simulacionesN = 10;W = randn(N,1);Y = 2 + W;Y_mean = 0;Y_var = 0;% estimo la mediafor i=1:N      Y_mean = Y_mean +Y(i) /N;
endforY_mean_N10 = Y_mean% estimo la varianza, divido por Nfor i=1:N      Y_var = Y_var + (Y(i) - Y_mean)^2 / N;endforY_varN_N10 = Y_varY_var = 0;% estimo la varianza, divido por N-1for i=1:N      Y_var = Y_var + (Y(i) - Y_mean)^2 / (N-1);endforY_varN_1_N10 = Y_varY_mean_teorico = 2Y_var_teorico = 1% numero de simulacionesN = 10000;W = randn(N,1);Y = 2 + W;Y_mean = 0;Y_var = 0;% estimo la mediafor i=1:N      Y_mean = Y_mean +Y(i) /N;endforY_mean_N10000= Y_mean% estimo la varianza, divido por Nfor i=1:N      Y_var = Y_var + (Y(i) - Y_mean)^2 / N;endforY_varN_N10000 = Y_varY_var = 0;% estimo la varianza, divido por N-1for i=1:N      Y_var = Y_var + (Y(i) - Y_mean)^2 / (N-1);endforY_varN_1_N10000= Y_varY_mean_teorico = 2Y_var_teorico = 1