% clase 4 ej 3% numero de simulacionesN = 1000;% proceso aleatorioX = randn(N,1) ;% numero de bins, debe ser par para este ejercicioM = 100;% vector de histogramahist_vect = zeros(M,1);% defino x_min y x_max de la v.a.x_min = -10;x_max = -x_min;% particiono el espacio muestral (truncado)x = linspace (x_min,x_max,M+1);% busco las ocurrencias de X dentro de xfor i=1:N      % busco en que bin "cae" X      pos = find(X(i) <=x);            % me quedo con el primer elemento      pos = pos(1);            % incremento el histograma      hist_vect(pos) = hist_vect(pos)  + 1;endfor% normalizo el histogramahist_vect = hist_vect  * M / N;% index for plottingn = linspace (x_min,x_max,M);plot(n,hist_vect)