pkg load statisticsrandn("seed", 1234)% numero de simulacionesn = 2000;% numero de muestrasN = 10;% media muestral% la usamos como media de distribucion en el ejerciciomu = 998.9;% dispersionsigma = 80;% el intervalo de confianza de 95% teorico es:mu_min_teorico = mu - 1.96*sigma/sqrt(N);mu_max_teorico = mu + 1.96*sigma/sqrt(N);% nivel de confianza del intervalo, deberia ser igual a 0.95 al final de la simulacionconfianza = 0;for i=1:n    % generar  una muestra de la distribucion de medias muestrales    X = sigma/sqrt(N)*randn+ mu;    %  chequeo cuantos valores estan en el intervalo de confianza    confianza = confianza + (1/n) *(X>=mu_min_teorico & X<=mu_max_teorico);end% estimacion del intervalo de confianzaconfianza_simulado = confianzaconfianza_teorico = 0.95%  estimacion de rango de intervalo de longitud 80% 80 = 2*z * sigma/sqrt(N)  , encontrar z de la normalz = 80*sqrt(N)/(2*sigma);% encuentro el area por encima de zA = 1-normcdf(z);% para a un intervalo doble, el area se duplica, entonces el porcentaje del intervalo  de confianza de longitud 80 esintervalo_80_teorico = 1- 2*A