pkg load statistics% CIMT - Hypothesis Testing, ejemplo de las bebidas% http://www.cimt.org.uk/projects/mepres/alevel/stats_ch10.pdf% la hipotesis nula es que los participantes eligen al azarp = 1/3;% nivel de significacionalpha = 0.05;% numero de personasN = 10;% minimo numero de personasN_min = 0;% probabilidad de necesitar i personas para el ensayop_i_personas = 0;% cuantas personas son necesarias para confirmar la hipotesis, busco desde el maximo hacia el minimo de personasfor i=N:-1:0    % buscar el numero de personas hasta con la cdf binomial hasta que se exceda alpha, y retornar el numero de personas anterior        % probabilidad de que 'i' personas deban acertar el ensayo    p_i_personas = 0;        % si i personas aciertan, i+1 incluyen a las i personas previas asi que las debo incluir    for j=i:N      p_i_personas = p_i_personas + binopdf(j,N,p);    endfor        % si esta probabilidad excede a alpha, entonces necesitamos una persona mas en el ensayo    if(p_i_personas>alpha)        % devolver el numero de personas previo (recordar que i es descendiente)        N_min = i+1;        break;    endif    endfor  disp('Minimo numero de personas:'), disp(N_min)
