pkg load statisticsclose allrand("seed",1234);betarnd("seed",1234);binornd("seed",1234);% numero de simulacionesn= 200;% numero de muestras de datos (enunciado)N = 10;% parametros de la distribucion a prioria = 3;b = 27;% sum x_i, dato de enunciadosum_xi = 0;% media a posterioritheta_x_posteriori = 0;      %  genero la distribucion a priori de theta, n experimentostheta_a_priori_vect = betarnd(a,b,[n,1]);% x_dado_theta_vectorx_dado_theta_vector= zeros(n,N);for i=1:n            % theta a priori      theta_a_priori = theta_a_priori_vect(i);      % genero la verosimilitud, N muestras por experimento      for j=1:N                    % uso binornd con k=1 tiradas para simular una variable Bernoulli con p=theta_a_priori            x_dado_theta_vector(i,j) = binornd(1,theta_a_priori);
       endfor       endfor  % la medicion fue x1,x2,...x10 = 0, entonces busco el numero de ocurrencias de esta realizacion  % sumo las filas del vector (cada fila es una realizacion)filas_sum_xi = sum(x_dado_theta_vector, 2);  % me fijo cuales filas me dieron cerofilas_sum_igual_sum_xi = find(filas_sum_xi== sum_xi);  % busco los valores de pi_theta en estas filas theta_a_posteriori = theta_a_priori_vect(filas_sum_igual_sum_xi);media_theta_a_posteriori = mean(theta_a_posteriori)
