% examen 2020 ejercicio 1% proceso Binomial% configuro la semilla inicial del proceso aleatorio uniformerand ("seed", 123);% moneda cargadap = 0.01;% numero de ensayosN = 1000;% numero maximo de fosforos por cajan = 68;% numero de fosforosn_fosforos_fallados = zeros(N,1);% hacemos N ensayos, en cada ensayo tiramos la moneda 10 veces% y vemos si al menos 3 es cecafor i = 1:N  % n tiradas por ensayo  for j = 1:n    if(rand() < p)      n_fosforos_fallados(i) = n_fosforos_fallados(i) + 1;    end  endend% media teoricamedia_teorica = n*p% calcular la media y dispersionmedia_muestral = sum(n_fosforos_fallados) / N% dispersion teoricadispersion_teorica = sqrt(n * p * (1-p))% dispersion muestral, estimador insesgadodispersion_muestral = sqrt((1/(N-1)) * sum((n_fosforos_fallados - media_muestral * ones(N,1)).^2))