% examen 2020 ejercicio 2% configuro la semilla inicial del proceso aleatorio uniformerand ("seed", 123);% numero de simulacionesN=100;% simular una variable aleatoria uniformeU = rand(N,1);% genero la variable aleatoria pedidaX= 3*sqrt(U);% simulaciones de la variable independienten = 1000;% variable independientex = linspace(0,3,n);% constante de filtrado (y dispersion de la Gaussiana)h = .5;% proceso aleatorio%  pdf estimadap = zeros(size(x));% para cada muestrafor i=1:N  % para cada variable independiente  for j=1:length(x)        % kernel function, Gaussian        K = (1/sqrt(2*pi))*exp(-(1/2)*((X(i)-x(j))/h)^2);        % estimation of pdf        p(j) = p(j) + (1/(N*h)) * K;    endforendfor      plot(x,p)